import { createContext, useContext, useState, ReactNode } from "react"

type ApprovalState = {
  approvals: { [transactionId: string]: boolean }
  setApprovals: React.Dispatch<React.SetStateAction<{ [transactionId: string]: boolean }>>
}

const defaultValue: ApprovalState = {
  approvals: {},
  setApprovals: () => {}, // Default function, will be overridden by the provider.
}

const ApprovalsContext = createContext<ApprovalState>(defaultValue)

export const useApprovals = () => {
  return useContext(ApprovalsContext)
}

type ApprovalsProviderProps = {
  children: ReactNode
}

export const ApprovalsProvider: React.FC<ApprovalsProviderProps> = ({ children }) => {
  const [approvals, setApprovals] = useState<{ [transactionId: string]: boolean }>({})

  return (
    <ApprovalsContext.Provider value={{ approvals, setApprovals }}>{children}</ApprovalsContext.Provider>
  )
}
